mapper.select:
select bd.id, bd.device_code, bd.mouse_code, bd.experiment_id, bd.behavior_type, bd.behavior_value, bd.confidence, bd.timestamp, bd.create_by, bd.create_time, bd.update_by, bd.update_time, bd.remark,
       d.device_name,
       m.mouse_name,
       e.experiment_name,
       u1.user_name as createByName, u2.user_name as updateByName
from t_behavior_data bd
left join t_device d on bd.device_code = d.device_code
left join t_mouse m on bd.mouse_code = m.mouse_code
left join t_experiment e on bd.experiment_id = e.id
left join sys_user u1 on bd.create_by = u1.user_name
left join sys_user u2 on bd.update_by = u2.user_name
where 1=1

    AND bd.device_code = #{deviceCode}


    AND bd.mouse_code = #{mouseCode}


    AND bd.experiment_id = #{experimentId}


    AND bd.behavior_type = #{behaviorType}


    AND bd.timestamp >= #{params.beginTimestamp}


    AND bd.timestamp <= #{params.endTimestamp}

order by bd.timestamp desc

mapper.update:
update t_behavior_data
device_code = #{deviceCode}, mouse_code = #{mouseCode}, experiment_id = #{experimentId}, behavior_type = #{behaviorType}, behavior_value = #{behaviorValue}, confidence = #{confidence}, timestamp = #{timestamp}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where id = #{id}
